	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collStudents) {
				foreach ((array) $this->collStudents as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collProfileExtras) {
				foreach ((array) $this->collProfileExtras as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collActivitysRelatedByClientId) {
				foreach ((array) $this->collActivitysRelatedByClientId as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collActivitysRelatedByCoachId) {
				foreach ((array) $this->collActivitysRelatedByCoachId as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collFeedbacks) {
				foreach ((array) $this->collFeedbacks as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collCourseDatess) {
				foreach ((array) $this->collCourseDatess as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collStudents = null;
		$this->collProfileExtras = null;
		$this->collActivitysRelatedByClientId = null;
		$this->collActivitysRelatedByCoachId = null;
		$this->collFeedbacks = null;
		$this->collCourseDatess = null;
	}


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseProfile:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseProfile::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


