	/**
	 * Method perform an INSERT on the database, given a Workshop or Criteria object.
	 *
	 * @param      mixed $values Criteria or Workshop object containing data that is used to create the INSERT statement.
	 * @param      PropelPDO $con the PropelPDO connection to use
	 * @return     mixed The new primary key.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doInsert($values, PropelPDO $con = null)
	{
    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseWorkshopPeer:doInsert:pre') as $sf_hook)
    {
      if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseWorkshopPeer', $values, $con))
      {
        return $sf_hook_retval;
      }
    }

		if ($con === null) {
			$con = Propel::getConnection(WorkshopPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from Workshop object
		}

		if ($criteria->containsKey(WorkshopPeer::ID) && $criteria->keyContainsValue(WorkshopPeer::ID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.WorkshopPeer::ID.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseWorkshopPeer:doInsert:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseWorkshopPeer', $values, $con, $pk);
    }

		return $pk;
	}

