			}

			if ($ret) {
				FlexformQuestionPeer::doDelete($this, $con);
				$this->postDelete($con);
				// symfony_behaviors behavior
				foreach (sfMixer::getCallables('BaseFlexformQuestion:delete:post') as $callable)
				{
