	/**
	 * Gets an array of FlexformAnswer objects which contain a foreign key that references this object.
	 *
	 * If this collection has already been initialized with an identical Criteria, it returns the collection.
	 * Otherwise if this FlexformQuestion has previously been saved, it will retrieve
	 * related FlexformAnswers from storage. If this FlexformQuestion is new, it will return
	 * an empty collection or the current collection, the criteria is ignored on a new object.
	 *
	 * @param      PropelPDO $con
	 * @param      Criteria $criteria
	 * @return     array FlexformAnswer[]
	 * @throws     PropelException
	 */
	public function getFlexformAnswers($criteria = null, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(FlexformQuestionPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collFlexformAnswers === null) {
			if ($this->isNew()) {
			   $this->collFlexformAnswers = array();
			} else {

				$criteria->add(FlexformAnswerPeer::FLEXFORM_QUESTION_ID, $this->id);

				FlexformAnswerPeer::addSelectColumns($criteria);
				$this->collFlexformAnswers = FlexformAnswerPeer::doSelect($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return the collection.


				$criteria->add(FlexformAnswerPeer::FLEXFORM_QUESTION_ID, $this->id);

				FlexformAnswerPeer::addSelectColumns($criteria);
				if (!isset($this->lastFlexformAnswerCriteria) || !$this->lastFlexformAnswerCriteria->equals($criteria)) {
					$this->collFlexformAnswers = FlexformAnswerPeer::doSelect($criteria, $con);
				}
			}
		}
		$this->lastFlexformAnswerCriteria = $criteria;
		return $this->collFlexformAnswers;
	}

	/**
	 * Returns the number of related FlexformAnswer objects.
	 *
	 * @param      Criteria $criteria
	 * @param      boolean $distinct
	 * @param      PropelPDO $con
	 * @return     int Count of related FlexformAnswer objects.
	 * @throws     PropelException
	 */
	public function countFlexformAnswers(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(FlexformQuestionPeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collFlexformAnswers === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(FlexformAnswerPeer::FLEXFORM_QUESTION_ID, $this->id);

				$count = FlexformAnswerPeer::doCount($criteria, false, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(FlexformAnswerPeer::FLEXFORM_QUESTION_ID, $this->id);

				if (!isset($this->lastFlexformAnswerCriteria) || !$this->lastFlexformAnswerCriteria->equals($criteria)) {
					$count = FlexformAnswerPeer::doCount($criteria, false, $con);
				} else {
					$count = count($this->collFlexformAnswers);
				}
			} else {
				$count = count($this->collFlexformAnswers);
			}
		}
		return $count;
	}

	/**
	 * Method called to associate a FlexformAnswer object to this object
	 * through the FlexformAnswer foreign key attribute.
	 *
	 * @param      FlexformAnswer $l FlexformAnswer
	 * @return     void
	 * @throws     PropelException
	 */
	public function addFlexformAnswer(FlexformAnswer $l)
	{
		if ($this->collFlexformAnswers === null) {
			$this->initFlexformAnswers();
		}
		if (!in_array($l, $this->collFlexformAnswers, true)) { // only add it if the **same** object is not already associated
			array_push($this->collFlexformAnswers, $l);
			$l->setFlexformQuestion($this);
		}
	}


