	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collFlexformAnswers) {
				foreach ((array) $this->collFlexformAnswers as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collFlexformAnswers = null;
