	public static function doInsert($values, PropelPDO $con = null)
	{
    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseFlexformQuestionPeer:doInsert:pre') as $sf_hook)
    {
      if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseFlexformQuestionPeer', $values, $con))
      {
        return $sf_hook_retval;
      }
    }

		if ($con === null) {
			$con = Propel::getConnection(FlexformQuestionPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from FlexformQuestion object
		}

		if ($criteria->containsKey(FlexformQuestionPeer::ID) && $criteria->keyContainsValue(FlexformQuestionPeer::ID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.FlexformQuestionPeer::ID.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseFlexformQuestionPeer:doInsert:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseFlexformQuestionPeer', $values, $con, $pk);
    }

		return $pk;
	}

