	public static function doUpdate($values, PropelPDO $con = null)
	{
    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseFlexformQuestionPeer:doUpdate:pre') as $sf_hook)
    {
      if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseFlexformQuestionPeer', $values, $con))
      {
        return $sf_hook_retval;
      }
    }

		if ($con === null) {
